INSERT INTO `yyy_article` VALUES ('14', '6', '1', 'admin', null, '产生随机字符串', '<p>CLTPHP产生随机字符串</p><pre>/**\n+----------------------------------------------------------\n&nbsp;*&nbsp;产生随机字串，可用来自动生成密码&nbsp;默认长度6位&nbsp;字母和数字混合\n+----------------------------------------------------------\n&nbsp;*&nbsp;@param&nbsp;string&nbsp;$len&nbsp;长度\n&nbsp;*&nbsp;@param&nbsp;string&nbsp;$type&nbsp;字串类型\n&nbsp;*&nbsp;0&nbsp;字母&nbsp;1&nbsp;数字&nbsp;其它&nbsp;混合\n&nbsp;*&nbsp;@param&nbsp;string&nbsp;$addChars&nbsp;额外字符\n+----------------------------------------------------------\n&nbsp;*&nbsp;@return&nbsp;string\n+----------------------------------------------------------\n&nbsp;*/\nfunction&nbsp;rand_string($len=6,$type=&#39;&#39;,$addChars=&#39;&#39;)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;$str&nbsp;=&#39;&#39;;\n&nbsp;&nbsp;&nbsp;&nbsp;switch($type)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;case&nbsp;0:\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$chars=&#39;ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz&#39;.$addChars;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;break;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;case&nbsp;1:\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$chars=&nbsp;str_repeat(&#39;0123456789&#39;,3);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;break;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;case&nbsp;2:\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$chars=&#39;ABCDEFGHIJKLMNOPQRSTUVWXYZ&#39;.$addChars;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;break;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;case&nbsp;3:\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$chars=&#39;abcdefghijklmnopqrstuvwxyz&#39;.$addChars;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;break;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;case&nbsp;4:\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$chars&nbsp;=&nbsp;&quot;们以我到他会作时要动国产的一是工就年阶义发成部民可出能方进在了不和有大这主中人上为来分生对于学下级地个用同行面说种过命度革而多子后自社加小机也经力线本电高量长党得实家定深法表着水理化争现所二起政三好十战无农使性前等反体合斗路图把结第里正新开论之物从当两些还天资事队批点育重其思与间内去因件日利相由压员气业代全组数果期导平各基或月毛然如应形想制心样干都向变关问比展那它最及外没看治提五解系林者米群头意只明四道马认次文通但条较克又公孔领军流入接席位情运器并飞原油放立题质指建区验活众很教决特此常石强极土少已根共直团统式转别造切九你取西持总料连任志观调七么山程百报更见必真保热委手改管处己将修支识病象几先老光专什六型具示复安带每东增则完风回南广劳轮科北打积车计给节做务被整联步类集号列温装即毫知轴研单色坚据速防史拉世设达尔场织历花受求传口断况采精金界品判参层止边清至万确究书术状厂须离再目海交权且儿青才证低越际八试规斯近注办布门铁需走议县兵固除般引齿千胜细影济白格效置推空配刀叶率述今选养德话查差半敌始片施响收华觉备名红续均药标记难存测士身紧液派准斤角降维板许破述技消底床田势端感往神便贺村构照容非搞亚磨族火段算适讲按值美态黄易彪服早班麦削信排台声该击素张密害侯草何树肥继右属市严径螺检左页抗苏显苦英快称坏移约巴材省黑武培著河帝仅针怎植京助升王眼她抓含苗副杂普谈围食射源例致酸旧却充足短划剂宣环落首尺波承粉践府鱼随考刻靠够满夫失包住促枝局菌杆周护岩师举曲春元超负砂封换太模贫减阳扬江析亩木言球朝医校古呢稻宋听唯输滑站另卫字鼓刚写刘微略范供阿块某功套友限项余倒卷创律雨让骨远帮初皮播优占死毒圈伟季训控激找叫云互跟裂粮粒母练塞钢顶策双留误础吸阻故寸盾晚丝女散焊功株亲院冷彻弹错散商视艺灭版烈零室轻血倍缺厘泵察绝富城冲喷壤简否柱李望盘磁雄似困巩益洲脱投送奴侧润盖挥距触星松送获兴独官混纪依未突架宽冬章湿偏纹吃执阀矿寨责熟稳夺硬价努翻奇甲预职评读背协损棉侵灰虽矛厚罗泥辟告卵箱掌氧恩爱停曾溶营终纲孟钱待尽俄缩沙退陈讨奋械载胞幼哪剥迫旋征槽倒握担仍呀鲜吧卡粗介钻逐弱脚怕盐末阴丰雾冠丙街莱贝辐肠付吉渗瑞惊顿挤秒悬姆烂森糖圣凹陶词迟蚕亿矩康遵牧遭幅园腔订香肉弟屋敏恢忘编印蜂急拿扩伤飞露核缘游振操央伍域甚迅辉异序免纸夜乡久隶缸夹念兰映沟乙吗儒杀汽磷艰晶插埃燃欢铁补咱芽永瓦倾阵碳演威附牙芽永瓦斜灌欧献顺猪洋腐请透司危括脉宜笑若尾束壮暴企菜穗楚汉愈绿拖牛份染既秋遍锻玉夏疗尖殖井费州访吹荣铜沿替滚客召旱悟刺脑措贯藏敢令隙炉壳硫煤迎铸粘探临薄旬善福纵择礼愿伏残雷延烟句纯渐耕跑泽慢栽鲁赤繁境潮横掉锥希池败船假亮谓托伙哲怀割摆贡呈劲财仪沉炼麻罪祖息车穿货销齐鼠抽画饲龙库守筑房歌寒喜哥洗蚀废纳腹乎录镜妇恶脂庄擦险赞钟摇典柄辩竹谷卖乱虚桥奥伯赶垂途额壁网截野遗静谋弄挂课镇妄盛耐援扎虑键归符庆聚绕摩忙舞遇索顾胶羊湖钉仁音迹碎伸灯避泛亡答勇频皇柳哈揭甘诺概宪浓岛袭谁洪谢炮浇斑讯懂灵蛋闭孩释乳巨徒私银伊景坦累匀霉杜乐勒隔弯绩招绍胡呼痛峰零柴簧午跳居尚丁秦稍追梁折耗碱殊岗挖氏刃剧堆赫荷胸衡勤膜篇登驻案刊秧缓凸役剪川雪链渔啦脸户洛孢勃盟买杨宗焦赛旗滤硅炭股坐蒸凝竟陷枪黎救冒暗洞犯筒您宋弧爆谬涂味津臂障褐陆啊健尊豆拔莫抵桑坡缝警挑污冰柬嘴啥饭塑寄赵喊垫丹渡耳刨虎笔稀昆浪萨茶滴浅拥穴覆伦娘吨浸袖珠雌妈紫戏塔锤震岁貌洁剖牢锋疑霸闪埔猛诉刷狠忽灾闹乔唐漏闻沈熔氯荒茎男凡抢像浆旁玻亦忠唱蒙予纷捕锁尤乘乌智淡允叛畜俘摸锈扫毕璃宝芯爷鉴秘净蒋钙肩腾枯抛轨堂拌爸循诱祝励肯酒绳穷塘燥泡袋朗喂铝软渠颗惯贸粪综墙趋彼届墨碍启逆卸航衣孙龄岭骗休借&quot;.$addChars;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;break;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;default&nbsp;:\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;默认去掉了容易混淆的字符oOLl和数字01，要添加请使用addChars参数\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$chars=&#39;ABCDEFGHIJKMNPQRSTUVWXYZabcdefghijkmnpqrstuvwxyz23456789&#39;.$addChars;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;break;\n&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;if($len&gt;10&nbsp;)&nbsp;{//位数过长重复字符串一定次数\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$chars=&nbsp;$type==1?&nbsp;str_repeat($chars,$len)&nbsp;:&nbsp;str_repeat($chars,5);\n&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;if($type!=4)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$chars&nbsp;&nbsp;&nbsp;=&nbsp;&nbsp;&nbsp;str_shuffle($chars);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$str&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;=&nbsp;&nbsp;&nbsp;substr($chars,0,$len);\n&nbsp;&nbsp;&nbsp;&nbsp;}else{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;中文随机字\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;for($i=0;$i&lt;$len;$i++){\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$str.=&nbsp;msubstr($chars,&nbsp;floor(mt_rand(0,mb_strlen($chars,&#39;utf-8&#39;)-1)),1);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;$str;\n}</pre><table border=\"0\" class=\"syntaxhighlighter  php layui-table\"><tbody><tr><td class=\"gutter\"><br/></td><td class=\"code\"><br/></td></tr></tbody></table>', '1', '0', '259', null, '1500867996', '1509763331');
INSERT INTO `yyy_article` VALUES ('15', '6', '1', 'admin', null, '字符串截取', '<pre>//字符串截取\nfunction&nbsp;str_cut($sourcestr,$cutlength,$suffix=&#39;...&#39;)\n{\n&nbsp;&nbsp;&nbsp;&nbsp;$returnstr=&#39;&#39;;\n&nbsp;&nbsp;&nbsp;&nbsp;$i=0;\n&nbsp;&nbsp;&nbsp;&nbsp;$n=0;\n&nbsp;&nbsp;&nbsp;&nbsp;$str_length=strlen($sourcestr);//字符串的字节数\n&nbsp;&nbsp;&nbsp;&nbsp;while&nbsp;(($n&lt;$cutlength)&nbsp;and&nbsp;($i&lt;=$str_length))\n&nbsp;&nbsp;&nbsp;&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$temp_str=substr($sourcestr,$i,1);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$ascnum=Ord($temp_str);//得到字符串中第$i位字符的ascii码\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if&nbsp;($ascnum&gt;=224)&nbsp;&nbsp;&nbsp;&nbsp;//如果ASCII位高与224，\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$returnstr=$returnstr.substr($sourcestr,$i,3);&nbsp;//根据UTF-8编码规范，将3个连续的字符计为单个字符\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$i=$i+3;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//实际Byte计为3\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$n++;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//字串长度计1\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;elseif&nbsp;($ascnum&gt;=192)&nbsp;//如果ASCII位高与192，\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$returnstr=$returnstr.substr($sourcestr,$i,2);&nbsp;//根据UTF-8编码规范，将2个连续的字符计为单个字符\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$i=$i+2;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//实际Byte计为2\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$n++;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//字串长度计1\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;elseif&nbsp;($ascnum&gt;=65&nbsp;&amp;&amp;&nbsp;$ascnum&lt;=90)&nbsp;//如果是大写字母，\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$returnstr=$returnstr.substr($sourcestr,$i,1);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$i=$i+1;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//实际的Byte数仍计1个\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$n++;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//但考虑整体美观，大写字母计成一个高位字符\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;else&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//其他情况下，包括小写字母和半角标点符号，\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$returnstr=$returnstr.substr($sourcestr,$i,1);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$i=$i+1;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//实际的Byte数计1个\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$n=$n+0.5;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//小写字母和半角标点等与半个高位字符宽...\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;if&nbsp;($n&gt;$cutlength){\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$returnstr&nbsp;=&nbsp;$returnstr&nbsp;.&nbsp;$suffix;//超过长度时在尾处加上省略号\n&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;$returnstr;\n}</pre>', '1', '0', '225', null, '1501031299', '1509763290');
INSERT INTO `yyy_article` VALUES ('17', '6', '1', 'admin', null, '判断当前访问的用户是  PC端  还是 手机端', '<pre>/**\n&nbsp;*&nbsp;判断当前访问的用户是&nbsp;&nbsp;PC端&nbsp;&nbsp;还是&nbsp;手机端&nbsp;&nbsp;返回true&nbsp;为手机端&nbsp;&nbsp;false&nbsp;为PC&nbsp;端\n&nbsp;*&nbsp;&nbsp;是否移动端访问访问\n&nbsp;*&nbsp;@return&nbsp;boolean\n&nbsp;*/\nfunction&nbsp;isMobile()\n{\n&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;如果有HTTP_X_WAP_PROFILE则一定是移动设备\n&nbsp;&nbsp;&nbsp;&nbsp;if&nbsp;(isset&nbsp;($_SERVER[&#39;HTTP_X_WAP_PROFILE&#39;]))\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;true;\n\n&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;如果via信息含有wap则一定是移动设备,部分服务商会屏蔽该信息\n&nbsp;&nbsp;&nbsp;&nbsp;if&nbsp;(isset&nbsp;($_SERVER[&#39;HTTP_VIA&#39;]))\n&nbsp;&nbsp;&nbsp;&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;找不到为flase,否则为true\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;stristr($_SERVER[&#39;HTTP_VIA&#39;],&nbsp;&quot;wap&quot;)&nbsp;?&nbsp;true&nbsp;:&nbsp;false;\n&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;脑残法，判断手机发送的客户端标志,兼容性有待提高\n&nbsp;&nbsp;&nbsp;&nbsp;if&nbsp;(isset&nbsp;($_SERVER[&#39;HTTP_USER_AGENT&#39;]))\n&nbsp;&nbsp;&nbsp;&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$clientkeywords&nbsp;=&nbsp;array&nbsp;(&#39;nokia&#39;,&#39;sony&#39;,&#39;ericsson&#39;,&#39;mot&#39;,&#39;samsung&#39;,&#39;htc&#39;,&#39;sgh&#39;,&#39;lg&#39;,&#39;sharp&#39;,&#39;sie-&#39;,&#39;philips&#39;,&#39;panasonic&#39;,&#39;alcatel&#39;,&#39;lenovo&#39;,&#39;iphone&#39;,&#39;ipod&#39;,&#39;blackberry&#39;,&#39;meizu&#39;,&#39;android&#39;,&#39;netfront&#39;,&#39;symbian&#39;,&#39;ucweb&#39;,&#39;windowsce&#39;,&#39;palm&#39;,&#39;operamini&#39;,&#39;operamobi&#39;,&#39;openwave&#39;,&#39;nexusone&#39;,&#39;cldc&#39;,&#39;midp&#39;,&#39;wap&#39;,&#39;mobile&#39;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;从HTTP_USER_AGENT中查找手机浏览器的关键字\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if&nbsp;(preg_match(&quot;/(&quot;&nbsp;.&nbsp;implode(&#39;|&#39;,&nbsp;$clientkeywords)&nbsp;.&nbsp;&quot;)/i&quot;,&nbsp;strtolower($_SERVER[&#39;HTTP_USER_AGENT&#39;])))\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;true;\n&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;协议法，因为有可能不准确，放到最后判断\n&nbsp;&nbsp;&nbsp;&nbsp;if&nbsp;(isset&nbsp;($_SERVER[&#39;HTTP_ACCEPT&#39;]))\n&nbsp;&nbsp;&nbsp;&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;如果只支持wml并且不支持html那一定是移动设备\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;如果支持wml和html但是wml在html之前则是移动设备\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if&nbsp;((strpos($_SERVER[&#39;HTTP_ACCEPT&#39;],&nbsp;&#39;vnd.wap.wml&#39;)&nbsp;!==&nbsp;false)&nbsp;&amp;&amp;&nbsp;(strpos($_SERVER[&#39;HTTP_ACCEPT&#39;],&nbsp;&#39;text/html&#39;)&nbsp;===&nbsp;false&nbsp;||&nbsp;(strpos($_SERVER[&#39;HTTP_ACCEPT&#39;],&nbsp;&#39;vnd.wap.wml&#39;)&nbsp;&lt;&nbsp;strpos($_SERVER[&#39;HTTP_ACCEPT&#39;],&nbsp;&#39;text/html&#39;))))\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;true;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;false;\n}</pre>', '1', '0', '144', null, '1501204163', '1509763369');
INSERT INTO `yyy_article` VALUES ('20', '6', '1', 'admin', null, 'PHP格式化字节大小', '<pre>/**\n&nbsp;*&nbsp;PHP格式化字节大小\n&nbsp;*&nbsp;@param&nbsp;&nbsp;number&nbsp;$size&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;字节数\n&nbsp;*&nbsp;@param&nbsp;&nbsp;string&nbsp;$delimiter&nbsp;数字和单位分隔符\n&nbsp;*&nbsp;@return&nbsp;string&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;格式化后的带单位的大小\n&nbsp;*/\nfunction&nbsp;format_bytes($size,&nbsp;$delimiter&nbsp;=&nbsp;&#39;&#39;)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;$units&nbsp;=&nbsp;array(&#39;B&#39;,&nbsp;&#39;KB&#39;,&nbsp;&#39;MB&#39;,&nbsp;&#39;GB&#39;,&nbsp;&#39;TB&#39;,&nbsp;&#39;PB&#39;);\n&nbsp;&nbsp;&nbsp;&nbsp;for&nbsp;($i&nbsp;=&nbsp;0;&nbsp;$size&nbsp;&gt;=&nbsp;1024&nbsp;&amp;&amp;&nbsp;$i&nbsp;&lt;&nbsp;5;&nbsp;$i++)&nbsp;$size&nbsp;/=&nbsp;1024;\n&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;round($size,&nbsp;2)&nbsp;.&nbsp;$delimiter&nbsp;.&nbsp;$units[$i];\n}</pre>', '1', '0', '134', null, '1502067689', '1509763348');
