DROP TABLE IF EXISTS `yyy_classify`;
CREATE TABLE `yyy_classify` (
  `id` smallint(5) unsigned NOT NULL AUTO_INCREMENT,
  `name` varchar(90) NOT NULL DEFAULT '' COMMENT '分类名称',
  `key` varchar(255) NOT NULL DEFAULT '' COMMENT '分类的关键字,可能是为了搜索',
  `desc` varchar(255) NOT NULL DEFAULT '' COMMENT '分类描述',
  `pid` smallint(5) unsigned NOT NULL DEFAULT '0' COMMENT '该分类的父类ID,取值于该表的cat_id字段',
  `sort` tinyint(1) unsigned NOT NULL DEFAULT '50' COMMENT '该分类在页面显示的顺序,数字越大顺序越靠后,同数字,id在前的先显示',
  `measure` varchar(15) NOT NULL DEFAULT '' COMMENT '该分类的计量单位',
  PRIMARY KEY (`id`),
  KEY `parent_id` (`pid`)
) ENGINE=MyISAM AUTO_INCREMENT=33 DEFAULT CHARSET=utf8;

